CREATE OR REPLACE ALGORITHM = UNDEFINED DEFINER = root@localhost SQL SECURITY INVOKER VIEW `employees` AS select `Thresher`.`Employees`.`Employees_PKEY` AS `id`,`Thresher`.`Employees`.`list_id` AS `employee_list_id`,`Thresher`.`Employees`.`Emp_NameFirst` AS `first_name`,`Thresher`.`Employees`.`Emp_NameLast` AS `last_name`,`Thresher`.`Employees`.`Emp_NameMiddle`, concat(`Thresher`.`Employees`.`Emp_NameLast`, ", ", `Thresher`.`Employees`.`Emp_NameFirst`) AS `full_name`, if((`Thresher`.`Employees`.`Emp_Status` = 'Active'),1,0) AS `is_active` from `Thresher`.`Employees` where (`Thresher`.`Employees`.`list_id` is not null)
